mapper.select:
SELECT SYS_DIV_CD -- 시스템구분 : EFM, ADM , MENU_SCR_DEV -- 메뉴화면구분 : M 메뉴 S 화면 N 네비게이션 , MENU_CODE -- 메뉴코드 : M + 업무구분(3) + 순번(3) , MENU_NAME -- 메뉴_화면명 , MENU_DEPTH -- 메뉴단계 , MENU_SEQ -- 메뉴노출순서 : 메뉴 depth 내 순서 , UPPER_MENU_CODE -- 상위메뉴코드 , FILE_PATH -- 화면경로 : /COM/COM002M00 , LOGIN_YN -- 로그인여부 , SCR_CTN -- 화면설명 , MENU_CSS -- 메뉴CSS , USE_END_DATE -- 사용종료일자 , USE_STRT_DATE -- 사용시작일자 , USE_YN -- 사용여부 , LAST_USER_ID -- 수정자 , LAST_CHG_DT -- 수정일자 FROM menu WHERE 1=1 AND USE_STRT_DATE <= DATE_FORMAT(NOW(), '%Y%m%d') AND USE_END_DATE >= DATE_FORMAT(NOW(), '%Y%m%d')
AND USE_YN = 'Y'
AND SYS_DIV_CD = #{systemType}
AND MENU_CODE LIKE CONCAT('%', #{menuCode}, '%')
AND MENU_NAME LIKE CONCAT('%', #{menuName}, '%')
AND UPPER_MENU_CODE LIKE CONCAT('%', #{upperMenuCode}, '%')
AND FILE_PATH LIKE CONCAT('%', #{filePath}, '%')
AND MENU_SCR_DEV = #{menuScrDev}
ORDER BY SYS_DIV_CD, MENU_CODE, MENU_DEPTH , MENU_SEQ, MENU_CODE
LIMIT #{startNum}, #{endNum}

mapper.delete:
DELETE FROM menu
WHERE 1=1
AND   SYS_DIV_CD = #{systemType}
AND   MENU_CODE = #{menuCode}

mapper.select:
SELECT m.system_type, m.menu_id, m.menu_name, m.menu_level, m.sort_order, m.parent_menu_id, get_menu_nm(m.parent_menu_id) as parent_menu_nm, m.screen_id, m.use_yn, m.display_yn, m.rgtr_user_id, m.rgtr_dt, m.last_user_id, m.last_chg_dt, m.menu_grp_code, s.mapping_code, s.screen_name as screen_nm, s.screen_path FROM menu_temp m left join screen s on m.screen_id = s.screen_id WHERE 1=1
AND m.system_type = #{systemType}
AND m.menu_level = #{menuLevel}
AND m.parent_menu_id = #{parentMenuId}
ORDER BY m.menu_grp_code, m.menu_level, m.sort_order

mapper.insert:
INSERT INTO menu_temp (
system_type,menu_name, menu_level, sort_order, parent_menu_id,
screen_id, use_yn, display_yn, rgtr_user_id, rgtr_dt,menu_grp_code
) VALUES (
#{systemType}, #{menuName}, #{menuLevel}, #{sortOrder}, #{parentMenuId},
#{screenId}, #{useYn}, #{displayYn}, #{rgtrUserId}, DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'),#{menuGrpCode}
)

mapper.update:
UPDATE menu_temp
SET menu_grp_code = #{menuGrpCode},
last_user_id = #{lastUserId}
WHERE menu_id = #{menuId}
