mapper.select:
SELECT SCREEN_ID -- 화면 ID , SCREEN_NAME -- 화면 이름 , SCREEN_PATH -- 화면 경로(URL 또는 라우트) , MAPPING_CODE -- 매핑 코드 (예: M001) , MEMO -- 비고 , USE_YN -- 사용 여부 (Y/N) , RGTR_USER_ID -- 등록자 ID , RGTR_DT -- 등록일시 , LAST_USER_ID -- 최종 수정자 ID , LAST_CHG_DT -- 최종 수정일시 FROM SCREEN WHERE 1=1 AND system_type = 'ADMIN'
AND USE_YN = #{useYn}
AND SCREEN_NAME LIKE CONCAT('%', #{screenName}, '%')
AND SCREEN_PATH LIKE CONCAT('%', #{screenPath}, '%')
ORDER BY SCREEN_ID
LIMIT #{startNum}, #{endNum}
